(()=>{
  return new Promise(async (resolve, reject)=>{
    try {

      // INPUT: {
      //   type: 'create_and_syndicate_data:Qmdsfkjsl..',
      //   data: {
      //     sharedNode: Node,
      //     permissionsNode: Node
      //     syndicateToNode: Node
      //   }
      // }

      let {
        sharedNode,
        permissionsNode,
        syndicateToNode
      } = INPUT.data;


      let requestCache = await universe.getRequestCache();

      let InternalUserNode;
      try {
        InternalUserNode = requestCache.keyvalue.InternalUserNode;
      }catch(err){
        console.error('Missing InternalUserNode', err);
      }
      if(!InternalUserNode){
        // Forgot to identify_via_token beforehand 
        console.error('Missing InternalUserNode for create_and_syndicate action');
        throw "Missing InternalUserNode for create_and_syndicate action";
        return false;
      }

      universe.console.log('Starting create_and_syndicate');

      // Check author matches myself (private_identity) 
      // - also used for getting our own identity 
      // - TODO 
      let MyPrivateIdentity = await universe.capabilities().privateIdentity();
      // let myPublicKey = MyPrivateIdentity.data.public;
      // let newChallenge = universe.uuidv4();

      // Create new temp_shared_node 
      // - get _id of new Node (used in signature) 
      let tmpNodeData = {
        //nodeId:  // root level? TODO: find put in correct folder? 
        name: universe.uuidv4(),
        type: "tmp_shared_node:Qmsdfkj",
        data: {
        }
      }
      let savedTmpNode = await universe.newNode(tmpNodeData);

      console.log('savedTmpNode:', savedTmpNode, savedTmpNode._id);

      // sourceId is the path back to the node 
      // - "type:location" i.e. "idtestid:sub@nick:internalIdhere"
      // - depends on the identity (public/private,etc.) 
      let sourceType;
      switch(MyPrivateIdentity.data.identity.split(':')[0]){
        case 'id':
          sourceType = 'idid'; // normal public identity, normal internal id, the "ideal" situation 
          break;
        case 'idtest':
          sourceType = 'idtestid';
          break;
        default:
          // unexpected! 
          console.error('Invalid ID type!, missing id or idtest of MyPrivateNode.data.identity');
          return false;
          break;
      }
      let sourceId = [sourceType, ':', MyPrivateIdentity.data.identity.split(':')[1], ':', savedTmpNode._id].join('')
      let sharedNodeData = {
        _id: sourceId, // GLOBAL id ("type":"address"), "idtestid:sub@nick:internalId" 
        name: universe.uuidv4(),
        type: sharedNode.type,
        data: sharedNode.data,
        v: sharedNode.data.v || 1, // version
        signer: MyPrivateIdentity.data.identity // "idtest:nick"
      }

      // Sign stringify({_id, type, data})
      let stringToSign = JSON.stringify(sharedNodeData);
      let SignedNode = await universe.loadAndRunCapability('rsa',{},{
        type: 'standard_capability_action:0.0.1:local:298j291bs',
        data: {
          action: 'sign',
          options: {
            stringToSign
          }
        }
      });

      let result = SignedNode.data; // base64-encoded string signature 

      // Add signature to shared_node.data.signature 
      sharedNodeData.sig = result; // base64-encoded string 

      // Add permissions node (save whatever was sent in) 
      // - permissions are checked in action "search_shared_data"
      //   - the resulting "syndication_permissions_ugly" has the matching to use for access requests 
      // - TODO: verify incoming permissions 
      // - TODO: internal-user permissions (using external for now?) 
      let newPermisionsNode = permissionsNode
      if(permissionsNode.type.indexOf('syndication_permissions:') !== 0){
        console.error('Invalid permissionsNode supplied'); 
        return false;
      } else {
        // Check validity of permissionsNode 
        let lodashQueries = [];
        for(let rule of permissionsNode.data.rules){
          let matchObj;
          switch(rule.type.split(':')[0]){

            case 'allow_group':

              matchObj = {
                "data.Identity.nodes": {
                  $elemMatch: {
                    type: 'identity_attributes:Qmdslfkjoi23f88f',
                    ["data." + rule.data.groupKey]: true
                  }
                }
              };
              lodashQueries.push(matchObj);
              break;


            case 'internal_private': // internal_private:Qm3289f0h23

              console.log('CHECKING internal_private:Qm3289f0h23');

              matchObj = {
                "data.Identity.nodes": {
                  $elemMatch: {
                    type: {
                      $like: 'internal_user:'
                    },
                    ["data.uuid"]: InternalUserNode.data.uuid
                  }
                }
              };
              lodashQueries.push(matchObj);

              break;


            default:
              console.error('Invalid permissionsNode rule type (only allow_groups allowed):', rule.type);
              continue;
          }
        }

        // Save "pretty" permissions node 
        // - read-only permissions at the moment! 
        permissionsNode.nodeId = savedTmpNode._id;
        let savedPermissionsNode = await universe.newNode(permissionsNode);

        // Save "ugly" permissions node (for actual processing) 
        let newUglyPermissionsNode = {
          nodeId: savedTmpNode._id,
          type: 'syndication_permissions_ugly:Qm2398h9sdhfcbvc23',
          data: {
            rules: [{
              type: 'lodash_query:Qmsdfj902932',
              data: JSON.stringify(lodashQueries.length > 1 ? {$and:lodashQueries}:lodashQueries[0])
            }]
          }
        }

        let savedUglyPermissionsNode = await universe.newNode(newUglyPermissionsNode);


        // Save blank "syndicated_to" node 
        let blankSyndicatedToNode = {
          nodeId: savedTmpNode._id,
          type: 'syndicated_to:Qmf3289h89f32',
          data: {
            seconds: {}
          }
        }

        let savedSyndicatedToNode = await universe.newNode(blankSyndicatedToNode);

      }

      // Update type to shared_node 
      // - update entire node locally 
      savedTmpNode.type = 'shared_node:Qmsdfjsdlfsj'; // not "tmp_shared_node" anymore 
      savedTmpNode.data = sharedNodeData;
      savedTmpNode = await universe.updateNode(savedTmpNode);


      // Run syndicateTo ("push" sharing) 
      // - TODO: figure out who I'm sharing to (match the "allow_groups" rules) 
      // - could have defaults here? 
      // - passed-in idenitities to push to ("idtest:x@y") 
      // let SyndicatedResult = await universe.loadAndRunCapability('Syndicate',{},{
      //   type: 'standard_capability_action:0.0.1:local:298j291bs',
      //   data: {
      //     action: 'everything', // or 'single' (with corresponding options) if syndicating a single new piece of data 
      //     options: {

      //     }
      //   }
      // });

      console.log('Testing Syndication after adding (single)');
      let SyndicatedResult = await universe.loadAndRunCapability('Syndicate',{},{
        type: 'standard_capability_action:0.0.1:local:298j291bs',
        data: {
          action: 'single', // or 'single' (with corresponding options) if syndicating a single new piece of data 
          options: {
            type: 'internal_node_id:Qmsdf23lf09j23',
            data: {
              nodeId: savedTmpNode._id
            }
          }
        }
      });




      universe.console.log('--Returning create_and_syndicate--');

      return resolve(savedTmpNode);



    }catch(err){
      console.error('FAILED create_and_syndicate',err);

      return resolve({
        // savedNode
        type: 'error:..',
        data: {
          msg: 'Failed create_and_syndicate',
          INPUT,
          error: true,
          err: err
        }
      });
      // resolve({ERROR: true, err: err});
    }


  })
})()
