          // console.log('Finding ExternalIdentity');

          // Get local ExternalIdentity if it exists 
          // - should exist, unless we deleted it! 
          cache = ['search-publickey', cacheAppId, publicKey].join('|');
          let nodes = await universe.searchMemory({
            cache,
            reCache: [{
              event: 'all',
              dataFilter: {
                type: {
                  $like: 'external_identity:'
                },
              }
            }],
            lean: true,
            filter: {
              // responseType: 'cjson',
              dataFilter: {
                _id: nodeId,
                type: {
                  $like: 'external_identity:'
