              }
            });
            // console.log('Finding internalUserId2');
            if(InternalUserNodes.length){
              InternalUserNode = InternalUserNodes[0];
            } else {
              console.error('Missing as InternalUser');
            }
            // console.log('Finding MyPrivateIdentity');
            let MyPrivateIdentity = await universe.loadAndRunCapability('Identity',{},{
              type: 'standard_capability_action:0.0.1:local:298j291bs',
              data: {
                action: 'getPrivate',
                options: {}
              }
            });
            // console.log('Finding MyPrivateIdentity2');

            // Get the ActAsExternalIdentityNode value 
