            cache = ['find-acts-user', cacheAppId, MyPrivateIdentity.data.identity].join('|');
            let ActAsExternalIdentityNodes = await universe.searchMemory({
              cache,
              lean: true,
              filter: {
                // responseType: 'cjson',
                sqlFilter: {
                  nodeId: null,
                  type: 'external_identity:0.0.1:local:8982f982j92',
                  data: {
                    identity: MyPrivateIdentity.data.identity
                  }
                }
              }
            });
            // console.log('Finding ActsAsExternal2');
            if(ActAsExternalIdentityNodes.length){
              ActAsExternalIdentityNode = ActAsExternalIdentityNodes[0];
            } else {
              console.error('Missing as ActAsExternalIdentityNode');
            }



          } else {
            // console.log('Skipping internalUserId');
          }

          break;

      }


      // set in request cache 
      // console.error('Setting requestCache:', ExternalIdentityNode._id);
      await universe.setRequestCacheKeyValue('IdentityNode',ExternalIdentityNode);
      await universe.setRequestCacheKeyValue('InternalUserNode',InternalUserNode);
      await universe.setRequestCacheKeyValue('ActAsExternalIdentityNode',ActAsExternalIdentityNode);

