        nodes.forEach(node=>{
          delete node._id;
          delete node.nodeId;
          delete node.parent;
          delete node.active;
          delete node.updatedAt;
          delete node.createdAt;

          removeExtra(node.nodes || []);
        });
      }

      removeExtra(nodes);

      let appBase = nodes[0];



