(()=>{
  return new Promise(async (resolve, reject)=>{
    try {

      console.log('--incoming_route_handler: View Identity Route (backend) --');

      var StellarSdk = universe.require('stellar-sdk');
      var crypto = universe.require('crypto');

      let page = {};

      // Load address and ipfs data 

      var horizonPossible = {
        public: {
          name: 'PubNet',
          address: 'https://horizon.stellar.org',
          network: 'public'
        },
        test: {
          name: 'TestNet',
          address: 'https://horizon-testnet.stellar.org',
          network: 'test'
        }
      };

      // load Route
      var routeFullPath = INPUT.data.req.path.substring(5);

      const { URL } = universe.require('url');
      var parser = new URL(routeFullPath);

      try {
        var protocol = parser.protocol;
        switch(protocol){
          case 'id:':
          case 'idtest:':
          case 'second:':
            parser.protocol = 'http:';
            break;

          default:
            page = "Invalid protocol. please use id:// or idtest:// or second://"
            throw "invalid protocol"
        }

        var baseIdentity = parser.host;
        var subname = parser.username || '';
        var password = parser.password.length ? parser.password : null;
        var routePath = parser.pathname ? parser.pathname.slice(1) : '';

        console.log('Parsed route:', {baseIdentity, subname, password, routePath});


        var stellarServer;

        switch(protocol){
          case 'id:':
            StellarSdk.Network.usePublicNetwork();
            stellarServer = new StellarSdk.Server(horizonPossible['public'].address);
            break;
          case 'idtest:':
            StellarSdk.Network.useTestNetwork();
            stellarServer = new StellarSdk.Server(horizonPossible['test'].address);
            break;
          case 'second:':
            StellarSdk.Network.useTestNetwork();
            stellarServer = new StellarSdk.Server(horizonPossible['test'].address); // TODO: change!
            break;
          case 'secondtest:':
            StellarSdk.Network.useTestNetwork();
            stellarServer = new StellarSdk.Server(horizonPossible['test'].address); // TODO: change!
            break;
          default:
            console.error('Invalid protocol');
            throw "invalid protocol"
        }

        console.log('stellarServer', stellarServer);

        var baseIdentity = baseIdentity.normalize('NFKC').toLowerCase();

        console.log('baseIdentity:', baseIdentity);

        var usernameSeed = crypto.createHash('sha256').update(baseIdentity).digest(); //returns a buffer
        // NFK
        console.log('usernameSeed', usernameSeed);

        var pairForIdentity = StellarSdk.Keypair.fromRawEd25519Seed(usernameSeed);

        console.log('pairForIdentity', pairForIdentity);

        console.log('Loading identity');

        let identityAccount = await stellarServer.loadAccount(pairForIdentity.publicKey())

        console.log('Identity loaded');

        console.log('identityAccount:', identityAccount);

