(()=>{
  return new Promise(async (resolve, reject)=>{
    try {

      console.log('---LINKING WEBSOCKET!---');

      // This is for when this Second is acting as the socketio Server, and a remote Second is a websocket client.
      // - done when we can't reach the remote Second via http, and they need to come a callin'
      // - This=>Cloud, Client=>RPi

      // Get the authenticated identity 

      let requestCache = await universe.getRequestCache();

      let ExternalIdentityNode;
      try {
        ExternalIdentityNode = requestCache.keyvalue.IdentityNode;
      }catch(err){
        console.error('Missing ExternalIdentityNode (IdentityNode in requestcache)', err);
      }
      if(!ExternalIdentityNode){
        // Forgot to identify_via_token beforehand 
        console.error('Missing IdentityNode for link_websocket_identity action');
        throw "Missing IdentityNode for link_websocket_identity action";
        return false;
      }


      // Assert that a websocket was used 
      let wsClientId = requestCache.keyvalue.wsClientId;


      // Get all the nodes for the ExternalIdentityNode
      let tmpExternal = await universe.searchMemory({
        filter: {
          sqlFilter: {
            _id: ExternalIdentityNode._id
          }
        }
      });

      ExternalIdentityNode = tmpExternal[0];


      // Update the external_identity's "local_websocket_connection:Qmf3289" w/ data: { clientId, status }
      // - TODO: enabled/disabled on connect/disconnect! 
      let wsNode = ExternalIdentityNode.nodes.find(n=>{
        return n.type.split(':')[0] == 'local_websocket_connection'
      });
      if(!wsNode){
        // create 
        console.log('Creating wsNode');
        wsNode = await universe.newNode({
          nodeId: ExternalIdentityNode._id,
          name: universe.uuidv4(),
          type: 'local_websocket_connection:Qmf3289',
          data: {
            isClient: true, // external identity is the client 
            clientId: wsClientId,
            status: 'connected' // TODO: update elsewhere?
          }
        });
      } else {
        // update 
        console.log('Updating wsNode');
        wsNode.data.isClient = true;
        wsNode.data.clientId = wsClientId;
        wsNode.data.status = 'connected'; // TODO: update elsewhere?
        wsNode = await universe.updateNode(wsNode);
      }


      return resolve({
        type: 'boolean:Qmdskfj20fi',
        data: { //true
          ExternalIdentityNode,
          wsClientId,
          wsNode
        }
      });


    }catch(err){
      resolve({ERROR: true, err: err.toString()});
    }


  })
})()
