(()=>{
  return new Promise(async (resolve, reject)=>{
    try {

      console.log('Listing Actions:', JSON.stringify(INPUT,null,2));

      let INPUT_data = INPUT.data || {};

      let finalReturnNodes = await universe.searchMemory({
        lean: true,
        filter: {
          dataFilter: {
            nodes : {
              $elemMatch: {
                type: {
                  $like: 'action_pointer:'
                }
              }
            }
          },
          // function for returning data from the Node, after filtering a bit 
          // - includes both the Node, and Nodes with nodeId (pointers) 
          filterNodes: tmpNodes=>{
            // this runs isolated, outside of the above context? (not sure) 
            return new Promise((resolve, reject)=>{
              tmpNodes = tmpNodes.filter(tmpNode=>{
                // see if has a ChildNode matching a type 

                if(!universe.sameAppPlatform(SELF, tmpNode)){
                  // console.log('FOUND IT UNDER SAME APP!!!!!', tmpNode._id);
                  return false;
                }

                return true;
              });
              resolve(tmpNodes);
            });
          },
        }
      });

      let returnNode;

      // show lean/trimmed by default 
      // - can also return cJSON (works), or a path (TODO)

      switch(INPUT_data.responseType){
        case 'cjson':
          // specifically requested *all* data 
          console.log('returning cJSON (not default)');
          returnNode = {
            type: 'list_of_nodes_as_cjson:0.0.1:local:3289f2',
            data: universe.cJSON.stringify(finalReturnNodes)
          }
          break;

        default:
          // lean/trim w/ default parents/children 
          try {
            console.log('Returning lean (default, for now?)');
            let tmpFinalReturnNodes = universe.trimSearchResults(finalReturnNodes);
            finalReturnNodes = tmpFinalReturnNodes;
            // console.log('trimmed search results ok');

            returnNode = {
              type: 'list_of_nodes:0.0.1:local:3289f2',
              data: finalReturnNodes
            }
            break;

          }catch(err){
            console.error('Failed trimSearchResults! this is bad, cuz we cant stringify the result now (dont know what to return)..', err);
            return;
          }

          break;

      }

      return resolve(returnNode);

    }catch(err){
      console.error('list_actions error:', err);
      resolve({ERROR: true, err: err.toString()});
    }


  })
})()
