(()=>{
  return new Promise(async (resolve, reject)=>{
    try {

      // heartbeat manager (scheduler) 
      // - "watches" for changes to heartbeat nodes, updates cron jobs accordingly (adds, removes, etc.) 
      let Cron = universe.require('cron');

      let cacheAppId = universe.getParentRoot(SELF)._id;
      let cache;

      // Expecting INPUT = capability_input_node:0.0.1:local:29f8239a13h9
      // - capabilityNode: Object,
      // - externalInputNode: Object,

      // Load the "Identify with Remote Second" capability
      // order_level:0.0.1:local:382hf273

      let cmdInputNode = INPUT.data.externalInputNode;

      if(cmdInputNode.type != 'standard_capability_action:0.0.1:local:298j291bs'){
        console.log('Unexpected input schema');
        return reject();
      }

      let inputAction = cmdInputNode.data.action;
      let inputOpts = cmdInputNode.data.options;


      async function findTasks(){
        // get tasks to watch and run  

        // app root-level (old: global-root) 
        let heartbeatNodes = await universe.searchMemory({
          // cache: ['scheduler-tasks', cacheAppId].join('|'),
          filter: {
            sqlFilter: {
              // nodeId: null // OLD: root-level
              type: 'heartbeat_task:Qmsfj032fjslfdk'
            }
          }
        });

        // console.log('Total Heartbeat Nodes:', heartbeatNodes.length);

        return heartbeatNodes;

      }

      switch(inputAction){
        case 'start':

          // startup manager 
          // - expecting to only start once! 
          console.log('Starting Scheduler (heartbeat manager)');

          // return resolve({
          //   type: 'boolean:...',
          //   data: true
          // });

          // heartbeat
          let idsToWatchForUpdates = [];

          async function updateRunningTasks(){

            idsToWatchForUpdates = [];
            let tasks = await findTasks();

            console.log('Total Tasks for Scheduler:', tasks.length);

            // clear any existing 
            universe.globalCache.heartbeatJobs = universe.globalCache.heartbeatJobs || {};
            Object.keys(universe.globalCache.heartbeatJobs).forEach(taskId=>{
              // console.log('=-=-=Stopping running job, restarting probably');
              universe.globalCache.heartbeatJobs[taskId].stop();
            })

            // Start cron job for each task 
            // - by ID of heartbeat node 
            let jobs = {};
            for(let task of tasks){
              if(!task.data.cron){
                console.error('Missing task.data.cron!');
                continue;
              }

              // console.log('Starting CronJob for task');

              try {

                // console.log('Running CodeNode for heartbeat1!');
                let codeNode = universe.lodash.find(task.nodes,{type:'code:0.0.1:local:32498h32f2'});

                idsToWatchForUpdates.push(task._id);
                idsToWatchForUpdates.push(codeNode._id);

                var job = new Cron.CronJob(task.data.cron, async function() {

                    // run in vm, and pass in the inputSchema Node! 
                    // - of the action! (expecting/requiring it to be a Node w/ code....how to verify that? logic vs data?) 
                    let taskResult;
                    try {
                      taskResult = await universe.runNodeCodeInVM({
                        codeNode, // includes type/schemaName and data 
                        dataNode: {}, // should be another Node that can be used by the action! 
                        timeout: 15 * 1000
                      });
                    }catch(err){
                      return resolve('FAILED code from taskResult');
                    }

                  }, async function () {
                    /* This function is executed when the job stops */

                  },
                  true, /* Start the job right now */
                  // timeZone /* Time zone of this job. */
                );
                jobs[task._id] = job;
              }catch(err){
                console.error('Invalid CRON pattern?', task.data.cron, err);
              }

            }

            universe.globalCache.heartbeatJobs = jobs;
          }

          updateRunningTasks();

          // Start listening for changes to heartbeat nodes 
          // - restart listening on changes
          universe.eventEmitter.on('node.afterUpdate', function _listener(emitData){
            // updated node is passed is as emitData (use emitData.type, emitData.data, emitData.nodeId, etc. for node contents) 
            // - TODO: only restart certain nodes, if data is updated, etc. 

            // console.log('afterUpdate in scheduler...'); //, idsToWatchForUpdates, JSON.stringify(emitData,null,2));

            if(idsToWatchForUpdates.indexOf(emitData._id) > -1){
              console.log('Updating Running Tasks!');
              updateRunningTasks();
            } else {
              // console.log('No need to update Running Tasks!');
            }

          })

          // ONLY returning a "boolean" response (no other URL is allowed besides this, for now) 
          return resolve({
            type: 'boolean:...',
            data: true
          });

        default:
          break;
      }

      return reject();

    }catch(err){
      resolve({ERROR: true, err: err.toString()});
    }


  })
})()
