            let matches = universe.lodash.query([{
              type: 'external_identity:0.0.1:local:8982f982j92',
              data: {
                identity: AuthIdentityNode.data.identity
              },
              nodes: [{
                type: 'internal_attributes:Qmsdfklj2f0', // TODO: apply attributes using internal Second (score, reputation, etc.)
