                data: attributesData
              }]
            }], JSON.parse(rule.match)); // JSON-stringified for handling dot-notation and regex across IPC 
            if(matches && matches.length){
              // apply using object.assign 
               attributesData = Object.assign(attributesData, rule.apply);
            }
          }
        }

        // final node to check against permissions 
        signers[signerExternalNode.data.identity] = {
          type: 'external_identity:0.0.1:local:8982f982j92',
          data: {
            identity: signerExternalNode.data.identity
          },
          nodes: [{
